import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';

export default class News extends Component {
  static defaultProps = {
    country: 'us',
    pageSize: 5,
    category: 'general',
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
    apiKey: PropTypes.string.isRequired,
    setProgress: PropTypes.func.isRequired,
  };

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
      error: null,
    };
    document.title = `${this.capitalizeFirstLetter(this.props.category)} - NewsApp`;
  }

  capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  async fetchNews() {
    this.props.setProgress(10);
    this.setState({ loading: true, error: null });

    // Add delay to prevent rate limiting
    await new Promise(resolve => setTimeout(resolve, 500));

    try {
      const { apiKey, country, pageSize, category } = this.props;
      const { page } = this.state;

      const url = new URL('http://api.mediastack.com/v1/news');
      url.searchParams.append('access_key', apiKey);
      url.searchParams.append('countries', country);
      url.searchParams.append('limit', pageSize);
      url.searchParams.append('offset', (page - 1) * pageSize);
      if (category !== 'general') {
        url.searchParams.append('categories', category);
      }

      this.props.setProgress(30);
      const response = await fetch(url);
      const data = await response.json();

      if (!response.ok || data.error) {
        throw new Error(data.error?.message || 'Failed to fetch news');
      }

      if (!data.data) {
        throw new Error('No news data available');
      }

      this.props.setProgress(70);
      return {
        articles: data.data,
        totalResults: data.pagination?.total || data.data.length,
      };
    } catch (error) {
      console.error('API Error:', error);
      throw error;
    }
  }

  async updateNews() {
    try {
      const newsData = await this.fetchNews();
      this.setState({
        articles: newsData.articles,
        totalResults: newsData.totalResults,
        loading: false,
      });
    } catch (error) {
      this.setState({
        loading: false,
        error: error.message,
        articles: [],
      });
    } finally {
      this.props.setProgress(100);
    }
  }

  async componentDidMount() {
    await this.updateNews();
  }

  handlePrevClick = async () => {
    this.setState(
      { page: this.state.page - 1 },
      async () => await this.updateNews()
    );
  };

  handleNextClick = async () => {
    this.setState(
      { page: this.state.page + 1 },
      async () => await this.updateNews()
    );
  };

  render() {
    const { articles, loading, page, totalResults, error } = this.state;
    const { pageSize } = this.props;

    return (
      <div className="container my-3">
        <h1 className="text-center" style={{ margin: '35px 0px' }}>
          NewsApp - Top {this.capitalizeFirstLetter(this.props.category)} Headlines
        </h1>

        {error && (
          <div className="alert alert-danger">
            Error: {error}. Please try again later.
          </div>
        )}

        {loading && <Spinner />}

        {!loading && !error && (
          <>
            <div className="row">
              {articles.map((article, index) => (
                <div className="col-md-4" key={article.url || index}>
                  <NewsItem
                    title={article.title || 'No title available'}
                    description={article.description || 'No description available'}
                    imageUrl={article.image || 'https://via.placeholder.com/300'}
                    newsUrl={article.url}
                    author={article.author || 'Unknown'}
                    date={article.published_at}
                    source={article.source}
                  />
                </div>
              ))}
            </div>

            <div className="container d-flex justify-content-between my-3">
              <button
                disabled={page <= 1}
                className="btn btn-dark"
                onClick={this.handlePrevClick}
              >
                &larr; Previous
              </button>
              <button
                disabled={page >= Math.ceil(totalResults / pageSize)}
                className="btn btn-dark"
                onClick={this.handleNextClick}
              >
                Next &rarr;
              </button>
            </div>
          </>
        )}
      </div>
    );
  }
}
