import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from "react-infinite-scroll-component";

export class News extends Component {
    static defaultProps = {
        country: 'in',
        pageSize: 8,
        category: 'general',
    }

    static propTypes = {
        country: PropTypes.string,
        pageSize: PropTypes.number,
        category: PropTypes.string,
        apiKey: PropTypes.string.isRequired,
        setProgress: PropTypes.func.isRequired,
    }

    capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }

    constructor(props) {
        super(props);
        this.state = {
            articles: [],
            loading: true,
            page: 1,
            totalResults: 0,
            error: null
        }
        document.title = `${this.capitalizeFirstLetter(this.props.category)} - NewsMonkey`;
    }

    buildApiUrl() {
        const { country, category, apiKey, pageSize } = this.props;
        const { page } = this.state;
        return `https://newsapi.org/v2/top-headlines?country=${country}&category=${category}&apiKey=${apiKey}&page=${page}&pageSize=${pageSize}`;
    }

    async fetchNewsData() {
        const url = this.buildApiUrl();
        const response = await fetch(url);
        
        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
        }
        
        return await response.json();
    }

    async updateNews() {
        try {
            this.props.setProgress(10);
            this.setState({ loading: true, error: null });
            
            const parsedData = await this.fetchNewsData();
            this.props.setProgress(70);
            
            this.setState({
                articles: parsedData.articles || [],
                totalResults: parsedData.totalResults || 0,
                loading: false,
            });
        } catch (error) {
            console.error("News fetch error:", error);
            this.setState({
                loading: false,
                error: error.message,
                articles: []
            });
        } finally {
            this.props.setProgress(100);
        }
    }

    async componentDidMount() {
        await this.updateNews();
    }

    fetchMoreData = async () => {
        try {
            this.setState({ page: this.state.page + 1 });
            const parsedData = await this.fetchNewsData();
            
            this.setState({
                articles: [...this.state.articles, ...(parsedData.articles || [])],
                totalResults: parsedData.totalResults || 0
            });
        } catch (error) {
            console.error("Error fetching more news:", error);
            this.setState({ error: error.message });
        }
    };

    renderNewsItems() {
        return this.state.articles.map((element, index) => (
            <div className="col-md-4" key={index}>
                <NewsItem
                    title={element.title || ""}
                    description={element.description || ""}
                    imageUrl={element.urlToImage}
                    newsUrl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source?.name || "Unknown"}
                />
            </div>
        ));
    }

    renderError() {
        return (
            <div className="container text-center my-5">
                <h2>Error Loading News</h2>
                <p className="text-danger">{this.state.error}</p>
                {this.state.error.includes("API") && (
                    <p>
                        Get a free API key from {' '}
                        <a href="https://newsapi.org" target="_blank" rel="noopener noreferrer">
                            newsapi.org
                        </a>
                    </p>
                )}
                <button 
                    className="btn btn-primary mt-3"
                    onClick={() => this.updateNews()}
                >
                    Retry
                </button>
            </div>
        );
    }

    render() {
        const { articles, loading, error, totalResults } = this.state;

        if (error) {
            return this.renderError();
        }

        if (loading && articles.length === 0) {
            return <Spinner />;
        }

        if (articles.length === 0 && !loading) {
            return (
                <div className="container text-center my-5">
                    <h2>No Articles Found</h2>
                    <p>Try refreshing or check back later.</p>
                </div>
            );
        }

        return (
            <>
                <h1 className="text-center" style={{ margin: '35px 0px', marginTop: '90px' }}>
                    NewsMonkey - Top {this.capitalizeFirstLetter(this.props.category)} Headlines
                </h1>
                
                <InfiniteScroll
                    dataLength={articles.length}
                    next={this.fetchMoreData}
                    hasMore={articles.length < totalResults}
                    loader={<Spinner />}
                >
                    <div className="container">
                        <div className="row">
                            {this.renderNewsItems()}
                        </div>
                    </div>
                </InfiniteScroll>
            </>
        );
    }
}

export default News;
